<?php


class Class_Ilf_Create_Admin_Menu
{
    public function __construct()
    {
        add_action('admin_menu', [$this, 'create_admin_menu']);
    }

    public function create_admin_menu()
    {
        $capability = 'manage_options';
        $slug = 'internal-link-flow';
        $icon_svg ='data:image/svg+xml;base64,';
        add_menu_page(
            __('Topical Flow', 'internallinkflow'),
            __('Topical Flow', 'internallinkflow'),
            $capability,
            $slug,
            [$this, 'menu_page_template'],
            $icon_svg
        );
    }

    public function menu_page_template()
    {
        echo '<div class="tailf-wrap"><div id="tailf-admin-app"></div></div>';
    }

}

new Class_Ilf_Create_Admin_Menu();